import{_ as e,c as a,o as r,af as n}from"./chunks/framework.CtP7Q83N.js";const i="/assets/stats_gear.C3RQX9RC.png",o="/assets/equipment_priority_list.8rigPRhS.png",u=JSON.parse('{"title":"Equipment System","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/systems/equipment.md","filePath":"wiki/systems/equipment.md"}'),s={name:"wiki/systems/equipment.md"};function d(l,t,g,c,p,m){return r(),a("div",null,t[0]||(t[0]=[n('<h1 id="equipment-system" tabindex="-1">Equipment System <a class="header-anchor" href="#equipment-system" aria-label="Permalink to &quot;Equipment System&quot;">​</a></h1><p><strong>Character Enhancement and Specialization</strong></p><p>The equipment system in RuneEra provides comprehensive character customization through specialized gear that enhances combat effectiveness, survival capabilities, and specialized roles. Each humanoid shares the same equipment framework, ensuring balanced progression and strategic diversity.</p><h2 id="equipment-slots" tabindex="-1">Equipment Slots <a class="header-anchor" href="#equipment-slots" aria-label="Permalink to &quot;Equipment Slots&quot;">​</a></h2><p><strong>Universal Equipment Framework</strong></p><p>All humanoids utilize the same equipment slot system, providing consistent upgrade paths and strategic equipment choices across different character types.</p><h3 id="combat-equipment" tabindex="-1">Combat Equipment <a class="header-anchor" href="#combat-equipment" aria-label="Permalink to &quot;Combat Equipment&quot;">​</a></h3><p><strong>Primary Combat Systems</strong></p><h4 id="weapons" tabindex="-1"><strong>Weapons</strong> <a class="header-anchor" href="#weapons" aria-label="Permalink to &quot;**Weapons**&quot;">​</a></h4><ul><li><strong>Primary Weapon</strong>: Main combat tool determining primary fighting style</li><li><strong>Secondary Weapon</strong>: Can equip a secondary weapon, only if primary is one-handed</li></ul><h4 id="armor-protection" tabindex="-1"><strong>Armor Protection</strong> <a class="header-anchor" href="#armor-protection" aria-label="Permalink to &quot;**Armor Protection**&quot;">​</a></h4><ul><li><strong>Head</strong>: Helmets, caps, and cranial protection</li><li><strong>Chest</strong>: Torso armor, robes, and upper body protection</li><li><strong>Legs</strong>: Lower body armor, pants, and leg protection</li><li><strong>Gloves</strong>: Hand protection, gauntlets, and grip enhancement</li></ul><h4 id="jewelry-and-accessories" tabindex="-1"><strong>Jewelry and Accessories</strong> <a class="header-anchor" href="#jewelry-and-accessories" aria-label="Permalink to &quot;**Jewelry and Accessories**&quot;">​</a></h4><ul><li><strong>Belt</strong></li><li><strong>Neck</strong></li><li><strong>Ring</strong></li><li><strong>Trinket</strong></li></ul><h4 id="survival-equipment" tabindex="-1"><strong>Survival Equipment</strong> <a class="header-anchor" href="#survival-equipment" aria-label="Permalink to &quot;**Survival Equipment**&quot;">​</a></h4><ul><li><strong>Bag</strong>: Inventory capacity expansion for resource management</li><li><strong>Gear</strong>: Environmental protection and specialized survival tools <ul><li><strong>Weather Protection</strong>: Enhanced resistance to environmental hazards</li><li><strong>Sleeping bag</strong>: Portable rest and recovery equipment</li></ul></li></ul><h2 id="weapon-categories" tabindex="-1"><strong>Weapon Categories</strong> <a class="header-anchor" href="#weapon-categories" aria-label="Permalink to &quot;**Weapon Categories**&quot;">​</a></h2><h3 id="melee-weapons" tabindex="-1"><strong>Melee Weapons</strong> <a class="header-anchor" href="#melee-weapons" aria-label="Permalink to &quot;**Melee Weapons**&quot;">​</a></h3><p><strong>One-Handed Melee:</strong></p><ul><li><strong>Fist</strong> - Unarmed combat, always available</li><li><strong>Sword</strong> - Balanced melee weapon with good versatility</li><li><strong>Mace</strong> - Blunt melee weapon with healing capabilities</li></ul><p><strong>Two-Handed Melee:</strong></p><ul><li><strong>Daggers</strong> - Fast, precise weapons for fast combat</li><li><strong>Spear</strong> - Long-reach melee weapon for tactical positioning</li><li><strong>Hammer</strong> - Heavy blunt weapon for devastating attacks</li><li><strong>Great Sword</strong> - Large sword for maximum melee damage</li></ul><h3 id="ranged-weapons" tabindex="-1"><strong>Ranged Weapons</strong> <a class="header-anchor" href="#ranged-weapons" aria-label="Permalink to &quot;**Ranged Weapons**&quot;">​</a></h3><ul><li><strong>Bow</strong> - Two-handed ranged weapon for precise long-distance combat</li><li><strong>Crossbow</strong> - One-handed ranged weapon allowing shield use</li></ul><h3 id="magic-weapons" tabindex="-1"><strong>Magic Weapons</strong> <a class="header-anchor" href="#magic-weapons" aria-label="Permalink to &quot;**Magic Weapons**&quot;">​</a></h3><ul><li><strong>Staff</strong> - Two-handed magic weapon with healing capabilities</li><li><strong>Scepter</strong> - One-handed magic weapon with healing capabilities</li></ul><h3 id="secondary-equipment" tabindex="-1"><strong>Secondary Equipment</strong> <a class="header-anchor" href="#secondary-equipment" aria-label="Permalink to &quot;**Secondary Equipment**&quot;">​</a></h3><ul><li><strong>Shield</strong> - Defensive equipment for protection and blocking</li><li><strong>Focus</strong> - Magical off-hand item for healing enhancement</li></ul><h3 id="attack-types" tabindex="-1"><strong>Attack Types</strong> <a class="header-anchor" href="#attack-types" aria-label="Permalink to &quot;**Attack Types**&quot;">​</a></h3><ul><li><strong>Melee:</strong> Close-range physical combat</li><li><strong>Ranged:</strong> Long-distance projectile attacks</li><li><strong>Magic:</strong> Spell-based attacks using magical energy</li></ul><h3 id="special-properties" tabindex="-1"><strong>Special Properties</strong> <a class="header-anchor" href="#special-properties" aria-label="Permalink to &quot;**Special Properties**&quot;">​</a></h3><ul><li><strong>Two-Handed:</strong> Requires both hands, cannot use with shield/focus</li><li><strong>Healing Capable:</strong> Can be used for healing abilities (Mace, Staff, Scepter)</li><li><strong>Primary/Secondary:</strong> Primary weapons are main combat tools, secondary provide support</li></ul><p><em>This weapon system allows for diverse combat builds and tactical choices based on your preferred playstyle and class abilities.</em></p><h2 id="armor-categories" tabindex="-1"><strong>Armor Categories</strong> <a class="header-anchor" href="#armor-categories" aria-label="Permalink to &quot;**Armor Categories**&quot;">​</a></h2><p><strong>Protection Specialization Systems</strong></p><p>The armor system provides three distinct protection philosophies, each offering unique advantages and trade-offs that cater to different combat styles and strategic approaches.</p><h3 id="heavy-armor" tabindex="-1"><strong>Heavy Armor</strong> <a class="header-anchor" href="#heavy-armor" aria-label="Permalink to &quot;**Heavy Armor**&quot;">​</a></h3><p><strong>Maximum Protection Specialization</strong></p><p><strong>Defensive Strengths</strong></p><ul><li><strong>Superior Damage Reduction</strong>: Excellent overall damage mitigation</li><li><strong>High Armor Rating</strong>: Maximum physical protection available</li><li><strong>Pierce Resistance</strong>: Strong protection against stabbing attacks</li><li><strong>Slash Resistance</strong>: Effective defense against cutting weapons</li><li><strong>Elemental Protection</strong>: Moderate resistance to magical damage</li><li><strong>Enhanced Endurance</strong>: Superior stamina and staying power</li></ul><p><strong>Tactical Limitations</strong></p><ul><li><strong>Reduced Mobility</strong>: Slower movement speed affects tactical positioning</li><li><strong>Blunt Vulnerability</strong>: Weakness to crushing and impact attacks</li></ul><p><strong>Strategic Applications</strong></p><ul><li><strong>Frontline Combat</strong>: Ideal for tank roles and direct confrontation</li><li><strong>Defensive Positions</strong>: Excellent for holding strategic locations</li><li><strong>Group Protection</strong>: Shielding allies through positioning and presence</li></ul><h3 id="medium-armor" tabindex="-1"><strong>Medium Armor</strong> <a class="header-anchor" href="#medium-armor" aria-label="Permalink to &quot;**Medium Armor**&quot;">​</a></h3><p><strong>Balanced Combat Approach</strong></p><p><strong>Versatile Advantages</strong></p><ul><li><strong>Moderate Protection</strong>: Good overall damage mitigation</li><li><strong>Enhanced Mobility</strong>: Improved movement speed for tactical flexibility</li><li><strong>Dodge Enhancement</strong>: Increased ability to avoid attacks entirely</li><li><strong>Elemental Resistance</strong>: Strong protection against magical attacks</li><li><strong>Balanced Endurance</strong>: Moderate endurance mitigation</li></ul><p><strong>Tactical Considerations</strong></p><ul><li><strong>Pierce Vulnerability</strong>: Weakness to stabbing and thrusting attacks</li><li><strong>Adaptable Performance</strong>: Suitable for various combat scenarios</li><li><strong>Tactical Flexibility</strong>: Good for changing battlefield roles</li></ul><p><strong>Strategic Applications</strong></p><ul><li><strong>Skirmish Combat</strong>: Excellent for mobile warfare and flanking</li><li><strong>Reconnaissance</strong>: Good protection without excessive mobility penalties</li><li><strong>Support Roles</strong>: Balanced capabilities for assisting allies</li></ul><h3 id="light-armor" tabindex="-1"><strong>Light Armor</strong> <a class="header-anchor" href="#light-armor" aria-label="Permalink to &quot;**Light Armor**&quot;">​</a></h3><p><strong>Speed and Agility Specialization</strong></p><p><strong>Mobility Advantages</strong></p><ul><li><strong>Maximum Speed</strong>: Fastest movement for tactical positioning</li><li><strong>Attack Speed</strong>: Increased combat tempo and strike frequency</li><li><strong>Enhanced Healing</strong>: Improved natural recovery and medical effectiveness</li><li><strong>Magical Resistance</strong>: Strong protection against elemental attacks</li></ul><p><strong>Protective Limitations</strong></p><ul><li><strong>Lower Physical Protection</strong>: Reduced defense against physical attacks</li><li><strong>Slash Vulnerability</strong>: Particular weakness to cutting weapons</li><li><strong>Minimal Endurance</strong>: Reduced endurance mitigation</li></ul><p><strong>Strategic Applications</strong></p><ul><li><strong>Hit-and-Run Tactics</strong>: Excellent for quick strikes and retreats</li><li><strong>Ranged Combat</strong>: Ideal for archers and magical combatants</li><li><strong>Healing Roles</strong>: Enhanced medical capabilities and recovery</li></ul><p>This comprehensive equipment system provides meaningful choices that significantly impact character performance, creating strategic depth where equipment decisions shape both individual effectiveness and group dynamics in the challenging world of RuneEra.</p><h2 id="offensive-stats" tabindex="-1">Offensive Stats <a class="header-anchor" href="#offensive-stats" aria-label="Permalink to &quot;Offensive Stats&quot;">​</a></h2><p>Combat statistics that determine a character&#39;s offensive capabilities, including damage output, accuracy, and elemental affinities.</p><table tabindex="0"><thead><tr><th>Stat</th><th>Description</th><th>Min</th><th>Max</th></tr></thead><tbody><tr><td><strong>Distance</strong></td><td>Attack range for ranged weapons/spears</td><td>-</td><td>-</td></tr><tr><td><strong>Attack</strong></td><td>Base attack power</td><td>-</td><td>-</td></tr><tr><td><strong>Healing</strong></td><td>Healing power for supportive abilities</td><td>-</td><td>-</td></tr><tr><td><strong>Penetration</strong></td><td>Armor penetration capability</td><td>-</td><td>-</td></tr><tr><td><strong>Hit Rate</strong></td><td>Chance to successfully hit target</td><td>-100%</td><td>100%</td></tr><tr><td><strong>Critical Rate</strong></td><td>Chance to deal critical damage</td><td>0%</td><td>75%</td></tr><tr><td><strong>Critical Damage</strong></td><td>Damage multiplier for critical hits</td><td>0%</td><td>150%</td></tr><tr><td><strong>Attack Speed</strong></td><td>Attack speed modifier</td><td>0%</td><td>150%</td></tr><tr><td><strong>Damage Multiplier</strong></td><td>Overall damage multiplier</td><td>-80%</td><td>300%</td></tr><tr><td><strong>Blunt Affinity</strong></td><td>Blunt damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Pierce Affinity</strong></td><td>Piercing damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Slash Affinity</strong></td><td>Slashing damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Fire Affinity</strong></td><td>Fire damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Water Affinity</strong></td><td>water/ice damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Electricity Affinity</strong></td><td>Lightning damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Nature Affinity</strong></td><td>Nature damage %</td><td>0%</td><td>200%</td></tr><tr><td><strong>Void Affinity</strong></td><td>Void damage %</td><td>0%</td><td>200%</td></tr></tbody></table><h2 id="defensive-stats" tabindex="-1">Defensive Stats <a class="header-anchor" href="#defensive-stats" aria-label="Permalink to &quot;Defensive Stats&quot;">​</a></h2><p>Combat statistics that determine a character&#39;s defensive capabilities, including damage mitigation, mobility, and resistance to various attack types.</p><table tabindex="0"><thead><tr><th>Stat</th><th>Description</th><th>Min</th><th>Max</th></tr></thead><tbody><tr><td><strong>Health</strong></td><td>Health points as value</td><td>-</td><td>-</td></tr><tr><td><strong>Armor</strong></td><td>Pierce armor protection</td><td>-</td><td>-</td></tr><tr><td><strong>Movement Speed Multiplier</strong></td><td>Movement speed in tiles/second</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Dodge Rate</strong></td><td>Chance to dodge incoming attacks</td><td>0%</td><td>100%</td></tr><tr><td><strong>Endurance Multiplier</strong></td><td>Endurance modifier</td><td>-80%</td><td>900%</td></tr><tr><td><strong>Buff Multiplier</strong></td><td>Effectiveness of buffs received</td><td>-80%</td><td>300%</td></tr><tr><td><strong>Damage Reduction Multiplier</strong></td><td>Reduces final damage received</td><td>-80%</td><td>200%</td></tr><tr><td><strong>Healing Multiplier</strong></td><td>Effectiveness of healing received</td><td>-90%</td><td>300%</td></tr><tr><td><strong>Blunt Resistance</strong></td><td>Resistance to blunt damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Pierce Resistance</strong></td><td>Resistance to piercing damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Slash Resistance</strong></td><td>Resistance to slashing damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Fire Resistance</strong></td><td>Resistance to fire damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Water Resistance</strong></td><td>Resistance to water/ice damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Electricity Resistance</strong></td><td>Resistance to lightning damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Nature Resistance</strong></td><td>Resistance to nature damage</td><td>-90%</td><td>200%</td></tr><tr><td><strong>Void Resistance</strong></td><td>Resistance to void damage</td><td>-90%</td><td>200%</td></tr></tbody></table><div class="tip custom-block"><p class="custom-block-title">Note</p><ul><li><strong>Modifiers</strong>: Stats can be modified by equipment rarity</li><li><strong>Aggregation</strong>: Multiple offensive stat sources are combined additively</li><li><strong>Limits</strong>: All percentage-based stats are capped at their maximum values during calculation</li></ul></div><h2 id="screenshot" tabindex="-1">Screenshot <a class="header-anchor" href="#screenshot" aria-label="Permalink to &quot;Screenshot&quot;">​</a></h2><p><img src="'+i+'" alt="Gear menu" data-zoomable="true"></p><p><img src="'+o+'" alt="Equipment priority" data-zoomable="true"></p>',71)]))}const f=e(s,[["render",d]]);export{u as __pageData,f as default};
