import{_ as t}from"./chunks/farming_zone.DcdB8sbO.js";import{_ as i,c as r,o as a,af as n}from"./chunks/framework.CtP7Q83N.js";const u=JSON.parse('{"title":"Farming & Gathering","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/systems/farming-gathering.md","filePath":"wiki/systems/farming-gathering.md"}'),o={name:"wiki/systems/farming-gathering.md"};function s(l,e,c,m,d,g){return a(),r("div",null,e[0]||(e[0]=[n('<h1 id="farming-gathering" tabindex="-1">Farming &amp; Gathering <a class="header-anchor" href="#farming-gathering" aria-label="Permalink to &quot;Farming &amp; Gathering&quot;">​</a></h1><p><em>Sustainable resource production through agriculture and herbalism</em></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Farming and gathering represent two distinct professions essential for long-term sustainability. Farming focuses on food crop production, while gathering specializes in herbs for alchemy and medicine.</p><h2 id="plant-varieties" tabindex="-1">Plant Varieties <a class="header-anchor" href="#plant-varieties" aria-label="Permalink to &quot;Plant Varieties&quot;">​</a></h2><p><strong>Agricultural Diversity</strong></p><ul><li><strong>18 different crops</strong> for food production and cooking</li><li><strong>15 herbs</strong> for alchemy, medicine, and specialized crafting</li><li><strong>10 tree types</strong> for lumber, special structures, and equipment</li></ul><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h2><p><strong>Seed Acquisition</strong></p><ul><li>Craft seeds from existing harvested resources</li><li>Harvest wild crops/herbs in the environment</li><li>Trade with nearby factions for initial stock</li><li>Establish sustainable seed production cycles</li></ul><h2 id="environmental-requirements" tabindex="-1">Environmental Requirements <a class="header-anchor" href="#environmental-requirements" aria-label="Permalink to &quot;Environmental Requirements&quot;">​</a></h2><p><strong>Critical Factors</strong></p><ul><li><strong>Season</strong>: Plant timing affects growth cycles</li><li><strong>Temperature</strong>: Plants die outside permitted ranges</li><li><strong>Biome compatibility</strong>: Wrong biome creates permanent growth penalties</li></ul><p><strong>Optimization Techniques</strong></p><ul><li>Plow tiles and create field floors for faster growth</li><li>Water crops regularly for optimal development</li><li>Apply fertilizer (crafted from wood and water in compost)</li></ul><h2 id="advanced-techniques" tabindex="-1">Advanced Techniques <a class="header-anchor" href="#advanced-techniques" aria-label="Permalink to &quot;Advanced Techniques&quot;">​</a></h2><p><strong>Greenhouse Technology</strong> <em>(Higher Tiers)</em></p><ul><li>Protected environment from weather and events</li><li>Reduced sunlight decreases growth speed</li><li>Requires maintained temperature control</li><li>Eliminates external environmental risks</li></ul><h2 id="challenges-solutions" tabindex="-1">Challenges &amp; Solutions <a class="header-anchor" href="#challenges-solutions" aria-label="Permalink to &quot;Challenges &amp; Solutions&quot;">​</a></h2><p><strong>Environmental Threats</strong></p><ul><li>Adverse weather conditions</li><li>Biome-specific events</li><li>Temperature fluctuations</li><li>Seasonal limitations</li></ul><p><strong>Management Strategies</strong></p><ul><li>Plan crop rotations around seasons</li><li>Maintain emergency food reserves</li><li>Invest in greenhouse infrastructure</li><li>Diversify crop types for resilience</li></ul><div class="tip custom-block"><p class="custom-block-title">Biome Selection</p><p>Always plant in appropriate biomes. The permanent growth penalty from wrong biome placement severely impacts long-term productivity.</p></div><div class="warning custom-block"><p class="custom-block-title">Temperature Management</p><p>Monitor temperature ranges carefully. Plant death from temperature extremes can devastate food supplies and herb production.</p></div><h2 id="screenshot" tabindex="-1">Screenshot <a class="header-anchor" href="#screenshot" aria-label="Permalink to &quot;Screenshot&quot;">​</a></h2><p><img src="'+t+'" alt="Farming zone" data-zoomable="true"></p>',27)]))}const f=i(o,[["render",s]]);export{u as __pageData,f as default};
