import{_ as a,a as i}from"./chunks/husbandry_management.Bixv4IHt.js";import{_ as t,c as r,o as n,af as s}from"./chunks/framework.CtP7Q83N.js";const b=JSON.parse('{"title":"Husbandry","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/systems/husbandry.md","filePath":"wiki/systems/husbandry.md"}'),o={name:"wiki/systems/husbandry.md"};function l(d,e,p,m,c,u){return n(),r("div",null,e[0]||(e[0]=[s('<h1 id="husbandry" tabindex="-1">Husbandry <a class="header-anchor" href="#husbandry" aria-label="Permalink to &quot;Husbandry&quot;">​</a></h1><p><em>Animal management for sustainable resource production and transportation</em></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Husbandry provides efficient leather and meat acquisition through strategic animal management. Success requires careful planning of breeding programs, habitat management, and transportation logistics.</p><h2 id="animal-varieties" tabindex="-1">Animal Varieties <a class="header-anchor" href="#animal-varieties" aria-label="Permalink to &quot;Animal Varieties&quot;">​</a></h2><p><strong>Wildlife Diversity</strong></p><ul><li><strong>50 different animals</strong> 10 for each biome</li><li><strong>Meat &amp; Hide</strong> There are 10 different meats and 10 different hides</li></ul><h2 id="getting-started" tabindex="-1">Getting Started <a class="header-anchor" href="#getting-started" aria-label="Permalink to &quot;Getting Started&quot;">​</a></h2><p><strong>Breeding Requirements</strong></p><ul><li>Tame animals of both sexes</li><li>House breeding pairs in enclosed spaces or pens</li><li>Maintain appropriate dietary needs for each species</li></ul><h2 id="animal-capture-management" tabindex="-1">Animal Capture &amp; Management <a class="header-anchor" href="#animal-capture-management" aria-label="Permalink to &quot;Animal Capture &amp; Management&quot;">​</a></h2><p><strong>Rope System</strong></p><ul><li><strong>Tamed animals</strong>: Simple time-based roping operation</li><li><strong>Wild animals</strong>: Requires lasso for initial capture</li><li>Roped animals become non-hostile and follow owner</li><li>Essential for transporting animals between biomes</li></ul><p><strong>Capture Strategy</strong></p><ul><li>Use caravans to transport captured animals home</li><li>Target animals from different biomes for variety</li></ul><h2 id="transportation-benefits" tabindex="-1">Transportation Benefits <a class="header-anchor" href="#transportation-benefits" aria-label="Permalink to &quot;Transportation Benefits&quot;">​</a></h2><p><strong>Caravan Enhancement</strong></p><ul><li>Tamed animals increase inventory weight capacity</li><li>Significantly faster caravan travel speeds</li><li>Essential for long-range exploration and trading</li></ul><h2 id="management-considerations" tabindex="-1">Management Considerations <a class="header-anchor" href="#management-considerations" aria-label="Permalink to &quot;Management Considerations&quot;">​</a></h2><p><strong>Habitat Requirements</strong></p><ul><li>Species-specific dietary needs</li><li>Predator-prey separation essential</li><li>Temperature comfort zones</li><li>Adequate space for breeding populations</li></ul><p><strong>Resource Planning</strong></p><ul><li>Sustainable breeding cycles for leather/meat</li><li>Transportation animal reserves</li><li>Feed production and storage</li><li>Seasonal care requirements</li></ul><div class="tip custom-block"><p class="custom-block-title">Breeding Strategy</p><p>Maintain breeding pairs of multiple species to ensure steady resource production and transportation capabilities.</p></div><div class="warning custom-block"><p class="custom-block-title">Predator Management</p><p>Never house predatory animals with prey species. This will result in lost livestock and disrupted breeding programs.</p></div><h2 id="screenshot" tabindex="-1">Screenshot <a class="header-anchor" href="#screenshot" aria-label="Permalink to &quot;Screenshot&quot;">​</a></h2><p><img src="'+a+'" alt="Husbandry" data-zoomable="true"></p><p><img src="'+i+'" alt="Husbandry Manageemnt" data-zoomable="true"></p>',28)]))}const f=t(o,[["render",l]]);export{b as __pageData,f as default};
