import{_ as e,c as a,o as n,af as i}from"./chunks/framework.CtP7Q83N.js";const g=JSON.parse('{"title":"Portal Network","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/systems/portal.md","filePath":"wiki/systems/portal.md"}'),o={name:"wiki/systems/portal.md"};function r(s,t,l,c,u,p){return n(),a("div",null,t[0]||(t[0]=[i('<h1 id="portal-network" tabindex="-1">Portal Network <a class="header-anchor" href="#portal-network" aria-label="Permalink to &quot;Portal Network&quot;">​</a></h1><p><em>Advanced magical infrastructure enabling fast travel and resource transportation across vast distances</em></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Portals represent the pinnacle of magical engineering, providing instant transportation between distant locations. These complex structures require significant investment in construction, activation, and maintenance, but offer unparalleled strategic mobility.</p><h2 id="portal-construction" tabindex="-1">Portal Construction <a class="header-anchor" href="#portal-construction" aria-label="Permalink to &quot;Portal Construction&quot;">​</a></h2><h3 id="construction-requirements" tabindex="-1">Construction Requirements <a class="header-anchor" href="#construction-requirements" aria-label="Permalink to &quot;Construction Requirements&quot;">​</a></h3><ul><li><strong>Materials</strong>: High-tier resources (planks, metal bars, stone blocks)</li><li><strong>Footprint</strong>: 3x3 tile area</li><li><strong>Build Time</strong>: Substantial construction duration</li><li><strong>Location</strong>: One portal per world area maximum</li></ul><h2 id="portal-activation" tabindex="-1">Portal Activation <a class="header-anchor" href="#portal-activation" aria-label="Permalink to &quot;Portal Activation&quot;">​</a></h2><h3 id="attunement-phase" tabindex="-1">Attunement Phase <a class="header-anchor" href="#attunement-phase" aria-label="Permalink to &quot;Attunement Phase&quot;">​</a></h3><p><strong>Initial Setup</strong></p><ul><li><strong>Cost</strong>: 80 mana (scaled by world settings)</li><li><strong>Duration</strong>: 14 hours attunement process</li><li><strong>Effect</strong>: Creates portal marker on world map</li><li><strong>Limitation</strong>: Only one portal per area can be attuned</li></ul><p><strong>Defensive Challenge</strong></p><ul><li>Attunement attracts undead swarm</li><li>Portal must be protected during vulnerable phase</li><li>Failed defense results in portal destruction</li></ul><h3 id="usage-generation" tabindex="-1">Usage Generation <a class="header-anchor" href="#usage-generation" aria-label="Permalink to &quot;Usage Generation&quot;">​</a></h3><p><strong>Charge System</strong></p><ul><li>Attuned portals automatically generate usage charges</li><li><strong>Cooldown</strong>: 4 days between charge generation</li><li><strong>Capacity</strong>: Maximum uses varies by portal tier</li></ul><h2 id="portal-operations" tabindex="-1">Portal Operations <a class="header-anchor" href="#portal-operations" aria-label="Permalink to &quot;Portal Operations&quot;">​</a></h2><h3 id="travel-connections" tabindex="-1">Travel Connections <a class="header-anchor" href="#travel-connections" aria-label="Permalink to &quot;Travel Connections&quot;">​</a></h3><p><strong>Personal Travel</strong></p><ul><li><strong>Cost</strong>: 30 mana per journey</li><li><strong>Preparation</strong>: 1 hour opening duration</li><li><strong>Active Window</strong>: 3 hours once opened</li><li><strong>Capacity</strong>: Transports units and roped animals together</li></ul><h3 id="item-transportation" tabindex="-1">Item Transportation <a class="header-anchor" href="#item-transportation" aria-label="Permalink to &quot;Item Transportation&quot;">​</a></h3><p><strong>Transfer Mode</strong></p><ul><li><strong>Cost</strong>: 15 mana per transfer</li><li><strong>Function</strong>: Swaps entire storage contents between portals</li><li><strong>Duration</strong>: Same opening requirements as travel</li><li><strong>Efficiency</strong>: Ideal for bulk resource management</li></ul><div class="warning custom-block"><p class="custom-block-title">Connection Requirements</p><ul><li>Can only link to other attuned portals</li></ul></div><h2 id="strategic-applications" tabindex="-1">Strategic Applications <a class="header-anchor" href="#strategic-applications" aria-label="Permalink to &quot;Strategic Applications&quot;">​</a></h2><h3 id="network-planning" tabindex="-1">Network Planning <a class="header-anchor" href="#network-planning" aria-label="Permalink to &quot;Network Planning&quot;">​</a></h3><p><strong>Location Selection</strong></p><ul><li>Strategic positioning for maximum coverage</li><li>Consider resource access points</li><li>Plan defensive positions around portals</li><li>Account for future expansion needs</li></ul><p><strong>Resource Management</strong></p><ul><li>Long-distance supply chains</li><li>Emergency resource deployment</li><li>Specialized material distribution</li><li>Settlement interconnection</li></ul><h3 id="operational-considerations" tabindex="-1">Operational Considerations <a class="header-anchor" href="#operational-considerations" aria-label="Permalink to &quot;Operational Considerations&quot;">​</a></h3><p><strong>Mana Economics</strong></p><ul><li>High operational costs require careful budgeting</li><li>Multiple portals create expensive but powerful network</li><li>Balance expansion with maintenance capabilities</li></ul><p><strong>Usage Optimization</strong></p><ul><li>Limited daily uses demand strategic planning</li><li>Coordinate travel and transport operations</li><li>Maintain mana reserves for emergency access</li></ul><h3 id="maintenance-requirements" tabindex="-1">Maintenance Requirements <a class="header-anchor" href="#maintenance-requirements" aria-label="Permalink to &quot;Maintenance Requirements&quot;">​</a></h3><ul><li>Regular mana investment for continued operation</li><li>Protection from hostile forces</li><li>Structural integrity monitoring</li><li>Network coordination management</li></ul><div class="tip custom-block"><p class="custom-block-title">Network Efficiency</p><p>Plan portal placement for maximum strategic value. Consider resource nodes, defensive positions, and future expansion when selecting locations.</p></div><div class="warning custom-block"><p class="custom-block-title">Attunement Vulnerability</p><p>The 14-hour attunement process attracts dangerous undead swarms. Ensure strong defenses are in place before beginning portal activation.</p></div><div class="info custom-block"><p class="custom-block-title">Usage Limits</p><p>Portal charges regenerate slowly (4-day cooldown). Plan long-distance operations carefully to avoid being stranded without transportation.</p></div>',40)]))}const m=e(o,[["render",r]]);export{g as __pageData,m as default};
