import{_ as t}from"./chunks/transportation.k3loiT3Q.js";import{_ as i,c as s,o as n,af as a}from"./chunks/framework.CtP7Q83N.js";const g=JSON.parse('{"title":"Settlements","description":"","frontmatter":{},"headers":[],"relativePath":"wiki/systems/settlement.md","filePath":"wiki/systems/settlement.md"}'),o={name:"wiki/systems/settlement.md"};function r(l,e,c,m,p,u){return n(),s("div",null,e[0]||(e[0]=[a('<h1 id="settlements" tabindex="-1">Settlements <a class="header-anchor" href="#settlements" aria-label="Permalink to &quot;Settlements&quot;">​</a></h1><p><em>Strategic expansion and resource management across multiple locations</em></p><h2 id="overview" tabindex="-1">Overview <a class="header-anchor" href="#overview" aria-label="Permalink to &quot;Overview&quot;">​</a></h2><p>Settlement expansion enables access to diverse biome resources and accelerates tier progression. While not mandatory, establishing outposts in different biomes provides significant strategic advantages through specialized resource access and mana generation.</p><h2 id="settlement-progression" tabindex="-1">Settlement Progression <a class="header-anchor" href="#settlement-progression" aria-label="Permalink to &quot;Settlement Progression&quot;">​</a></h2><p><strong>Initial Setup</strong></p><ul><li>Begin with single settlement</li><li>Unlock additional settlement capacity through technology research</li><li>Each new settlement requires initial mana investment</li><li>Establishment costs scale with area tier level</li></ul><h2 id="strategic-considerations" tabindex="-1">Strategic Considerations <a class="header-anchor" href="#strategic-considerations" aria-label="Permalink to &quot;Strategic Considerations&quot;">​</a></h2><p><strong>Biome Diversification</strong></p><ul><li>Different biomes offer unique resources and opportunities</li><li>Specialized settlements enable tier-specific material access</li><li>Reduces dependency on trade for essential materials</li><li>Provides strategic redundancy for critical resources</li></ul><p><strong>Management Approaches</strong></p><ul><li><strong>Full settlements</strong>: Complete infrastructure and population</li><li><strong>Outposts</strong>: Smaller, specialized resource extraction points</li><li><strong>Portal networks</strong>: Connect distant settlements for efficient resource and humanoid transport</li></ul><h2 id="settlement-benefits" tabindex="-1">Settlement Benefits <a class="header-anchor" href="#settlement-benefits" aria-label="Permalink to &quot;Settlement Benefits&quot;">​</a></h2><p><strong>Resource Access</strong></p><ul><li>Biome-specific materials and opportunities</li><li>Reduced transportation costs for specialized resources</li><li>Access to unique environmental conditions</li></ul><p><strong>Mana Generation</strong></p><ul><li>Pylon mana gathering capabilities</li><li>Essence processing for progression requirements</li><li>Strategic positioning for mana network optimization</li></ul><h2 id="event-quest-system" tabindex="-1">Event &amp; Quest System <a class="header-anchor" href="#event-quest-system" aria-label="Permalink to &quot;Event &amp; Quest System&quot;">​</a></h2><p><strong>Dynamic Content</strong></p><ul><li>Settlements attract events and quests</li><li>Content distribution weighted by humanoid population</li><li>Larger settlements receive more significant opportunities</li><li>Multiple settlements do not increase overall content generation</li></ul><h2 id="management-challenges" tabindex="-1">Management Challenges <a class="header-anchor" href="#management-challenges" aria-label="Permalink to &quot;Management Challenges&quot;">​</a></h2><p><strong>Complexity Scaling</strong></p><ul><li>Multiple settlements increase management overhead</li><li>Resource distribution and logistics coordination</li><li>Population allocation and specialization decisions</li><li>Portal network planning and maintenance</li></ul><div class="tip custom-block"><p class="custom-block-title">Outpost Strategy</p><p>Consider establishing smaller outposts connected by portals rather than full settlements. This provides biome access while minimizing management complexity.</p></div><div class="warning custom-block"><p class="custom-block-title">Mana Investment</p><p>Higher tier areas require substantial mana investment for settlement establishment. Plan expansion timing carefully to avoid resource shortages.</p></div><h2 id="screenshots" tabindex="-1">Screenshots <a class="header-anchor" href="#screenshots" aria-label="Permalink to &quot;Screenshots&quot;">​</a></h2><p><img src="'+t+'" alt="Transportation" data-zoomable="true"></p>',27)]))}const f=i(o,[["render",r]]);export{g as __pageData,f as default};
